mapper.select:
SELECT


,shangjia.shangjia_name as shangjiaName
,shangjia.shangjia_phone as shangjiaPhone
,shangjia.shangjia_email as shangjiaEmail
,shangjia.shangjia_photo as shangjiaPhoto
,shangjia.shangjia_xingji_types as shangjiaXingjiTypes
,shangjia.new_money as newMoney
,shangjia.shangjia_content as shangjiaContent
,shangjia.shangjia_delete as shangjiaDelete
,shangjia.jinyong_types as jinyongTypes

FROM meishi  a
left JOIN shangjia shangjia ON a.shangjia_id = shangjia.id



        and a.id in

        #{item}



        and (
            a.shangjia_id = #{params.shangjiaId}
        )


        and a.meishi_name like CONCAT('%',#{params.meishiName},'%')


        and a.meishi_uuid_number like CONCAT('%',#{params.meishiUuidNumber},'%')


          and a.zan_number >= #{params.zanNumberStart}   


          and a.zan_number <= #{params.zanNumberEnd}   


        and a.zan_number = #{params.zanNumber}


          and a.cai_number >= #{params.caiNumberStart}   


          and a.cai_number <= #{params.caiNumberEnd}   


        and a.cai_number = #{params.caiNumber}


        and a.meishi_types = #{params.meishiTypes}


          and a.meishi_kucun_number >= #{params.meishiKucunNumberStart}   


          and a.meishi_kucun_number <= #{params.meishiKucunNumberEnd}   


        and a.meishi_kucun_number = #{params.meishiKucunNumber}


          and a.meishi_old_money >= #{params.meishiOldMoneyStart}   


          and a.meishi_old_money <= #{params.meishiOldMoneyEnd}   


        and a.meishi_old_money = #{params.meishiOldMoney}


          and a.meishi_new_money >= #{params.meishiNewMoneyStart}   


          and a.meishi_new_money <= #{params.meishiNewMoneyEnd}   


        and a.meishi_new_money = #{params.meishiNewMoney}


          and a.meishi_clicknum >= #{params.meishiClicknumStart}   


          and a.meishi_clicknum <= #{params.meishiClicknumEnd}   


        and a.meishi_clicknum = #{params.meishiClicknum}


        and a.meishi_content like CONCAT('%',#{params.meishiContent},'%')


        and a.shangxia_types = #{params.shangxiaTypes}


          and a.meishi_delete >= #{params.meishiDeleteStart}   


          and a.meishi_delete <= #{params.meishiDeleteEnd}   


        and a.meishi_delete = #{params.meishiDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.shangjia_id IS NOT NULL


        and shangjia.shangjia_name like CONCAT('%',#{params.shangjiaName},'%')


        and shangjia.shangjia_phone like CONCAT('%',#{params.shangjiaPhone},'%')


        and shangjia.shangjia_email like CONCAT('%',#{params.shangjiaEmail},'%')


        and shangjia.shangjia_xingji_types = #{params.shangjiaXingjiTypes}



          and shangjia.new_money >= #{params.newMoneyStart}   


          and shangjia.new_money <= #{params.newMoneyEnd}   


        and a.new_money = #{params.newMoney}


        and shangjia.shangjia_content like CONCAT('%',#{params.shangjiaContent},'%')


          and shangjia.shangjia_delete >= #{params.shangjiaDeleteStart}   


          and shangjia.shangjia_delete <= #{params.shangjiaDeleteEnd}   


        and shangjia.shangjia_delete = #{params.shangjiaDelete}


        and shangjia.jinyong_types = #{params.jinyongTypes}



order by a.${params.sort} ${params.order}
